import React, { useState, useEffect } from 'react';

function Grievance() {
  const [activeTab, setActiveTab] = useState('submit');
  const [grievances, setGrievances] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const API_URL = process.env.REACT_APP_API_URL || '';

  const fetchGrievances = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await fetch(`${API_URL}/api/grievances`);
      const data = await res.json();

      if (!res.ok) throw new Error(data.error || 'Failed to fetch grievances');

      setGrievances(data.data);
    } catch (err) {
      console.error(err);
      setError(err.message || 'Server error');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const title = e.target.title.value.trim();
    const description = e.target.description.value.trim();

    if (!title || !description) {
      alert('Title and Description are required.');
      return;
    }

    try {
      const res = await fetch(`${API_URL}/api/grievances`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ title, description }),
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.error || 'Failed to submit grievance');

      alert('Grievance submitted successfully!');
      setGrievances((prev) => [...prev, data.data]);
      e.target.reset();
    } catch (err) {
      console.error(err);
      alert(err.message || 'Server error');
    }
  };

  useEffect(() => {
    if (activeTab === 'view') fetchGrievances();
  }, [activeTab]);

  const exportGrievancesToCSV = () => {
    let csv = 'ID,Title,Description,Status,Date\n';
    grievances.forEach((g) => {
      const row = [
        g._id || 'N/A',
        `"${g.title || ''}"`,
        `"${g.description || ''}"`,
        g.status || 'Pending',
        new Date(g.createdAt).toLocaleDateString(),
      ].join(',');
      csv += `${row}\n`;
    });

    const blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.setAttribute('download', 'grievance_report.csv');
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div>
      <h1>Grievance Portal</h1>
      <div className="grievance-buttons">
        {['submit', 'view'].map((tab) => (
          <button
            key={tab}
            className={activeTab === tab ? 'active' : ''}
            onClick={() => setActiveTab(tab)}
          >
            {tab === 'submit' ? 'Submit Grievance' : 'View Grievances'}
          </button>
        ))}
        <button
          className={activeTab === 'report' ? 'active' : ''}
          onClick={() => {
            setActiveTab('view'); // Ensure data is loaded before export
            exportGrievancesToCSV();
          }}
        >
          Grievance Report
        </button>
      </div>

      <div className="grievance-content">
        {activeTab === 'submit' && (
          <div>
            <h2>Submit Your Grievance</h2>
            <form onSubmit={handleSubmit}>
              <div>
                <label htmlFor="title">Title:</label>
                <input type="text" id="title" name="title" required />
              </div>
              <div>
                <label htmlFor="description">Description:</label>
                <textarea id="description" name="description" required></textarea>
              </div>
              <button type="submit">Submit</button>
            </form>
          </div>
        )}

        {activeTab === 'view' && (
          <div>
            <h2>Your Grievances</h2>
            {loading ? (
              <p>Loading...</p>
            ) : error ? (
              <p style={{ color: 'red' }}>{error}</p>
            ) : grievances.length ? (
              <ul>
                {grievances.map((g) => (
                  <li key={g._id}>
                    <strong>{g.title}</strong>: {g.description} â€”{' '}
                    <em>Status: {g.status || 'Pending'}</em>
                  </li>
                ))}
              </ul>
            ) : (
              <p>No grievances found.</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default Grievance;
